CREATE TABLE "asset_vehiclefile" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "updatedAt" datetime NOT NULL, "updatedBy" varchar(512) NOT NULL, "createdAt" datetime NOT NULL, "createdBy" varchar(512) NOT NULL, "description" text NOT NULL, "asset_id" bigint NULL REFERENCES "asset_asset" ("id") DEFERRABLE INITIALLY DEFERRED, "vehicleFile" varchar(100) NOT NULL);
CREATE TABLE "asset_roadtax" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "updatedAt" datetime NOT NULL, "updatedBy" varchar(512) NOT NULL, "createdAt" datetime NOT NULL, "createdBy" varchar(512) NOT NULL, "roadTaxId" varchar(256) NOT NULL, "startDate" datetime NULL, "endDate" datetime NULL, "roadTaxFiles" varchar(100) NOT NULL, "asset_id" bigint NULL REFERENCES "asset_asset" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "asset_registerlicense" ("license_ptr_id" bigint NOT NULL PRIMARY KEY REFERENCES "asset_license" ("id") DEFERRABLE INITIALLY DEFERRED, "firstRegisterDate" datetime NULL, "originalRegisterDate" datetime NOT NULL, "effectiveDate" datetime NULL);
CREATE TABLE "asset_license" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "updatedAt" datetime NOT NULL, "updatedBy" varchar(512) NOT NULL, "createdAt" datetime NOT NULL, "createdBy" varchar(512) NOT NULL, "name" varchar(256) NOT NULL, "description" text NOT NULL, "licenseFile" varchar(100) NOT NULL, "asset_id" bigint NULL REFERENCES "asset_asset" ("id") DEFERRABLE INITIALLY DEFERRED);